from

    t_mete_ns_day_data t
    t_mete_ns_day_wea t

join t_mete_station b ON t.stationNo = b.device_id where ${condition}
and

    DATE_FORMAT(t.ObserverTime,'%m%d')  between #{sT} and #{eT} and
    t.ObserverTime between #{startDate} and #{endDate} and


    t.${element} between #{min} and #{max}
    t.wep_code = #{code}

from

    t_mete_ns_day_data t
    t_mete_ns_day_wea t

join t_mete_station b ON t.stationNo = b.device_id where ${condition}
and
DATE_FORMAT(t.ObserverTime,'%m%d')  between '0101' and #{eT} and

    t.${element} between #{min} and #{max}
    t.wep_code = #{code}

from

    t_mete_ns_day_data t
    t_mete_ns_day_wea t

join t_mete_station b ON t.stationNo = b.device_id where ${condition}
and
DATE_FORMAT(t.ObserverTime,'%m%d')  between #{sT} and '1231' and

    t.${element} between #{min} and #{max}
    t.wep_code = #{code}
